package TASKS;

public class OddNumber1_99 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Odd numbers from 1 to 99\n----------------------------");
	
    for(int i=1;i<=99;i++)
    {
    	if(i%2!=0)
    	{
    		System.out.print(i+" ");
    	}
    }
	}

}
